  const [queryText,setQueryText]=useState("")
  //   useEffect(()=>{
  // fetch('https://restcountries.com/v3.1/all')
  // .then(response=>response.json())
  // .then(response=>setCountrie(response));//get request yollamk ıstedıgımız adresı alır parametre ıle 
  //   },[])
  fetch('https://jsonplaceholder.typicode.com/posts/1')
  .then((response) => response.json())
  .then((json) => console.log(json));
  useEffect(() => {
    axios
    .get('https://jsonplaceholder.typicode.com/comments')
      .then(response => setCountrie(response.data))
      .catch(error => console.log({ error }));
  }, []);
  useEffect(()=>{
    fetchData() //basta boş oldugu ıcın
    },[queryText]) //her querytext değiştiğinde fetch datayı cagırsın
  return (
    <div className="App">
       <input value={queryText} placeholder="ara" onChange={(e)=>{setQueryText(e.target.value)}} className="fgh"/> 
      <h1>fetch()-axios dersleri</h1>
      {countries.map(country => {
        return (
          <div key={country.name}>
            <h2> {country.name} </h2>
            <h4>{country.email} </h4>
            <p>
            {country.body}
            </p>
          </div>
        )
      })}
    </div>
  );
}

export default App;
