import axios from 'axios';
import React, {useEffect,useState} from 'react';
n

function App() {
  const [countries,setCountrie]=useState([]);

//   useEffect(()=>{
// fetch('https://restcountries.com/v3.1/all')
// .then(response=>response.json())
// .then(response=>setCountrie(response));//get request yollamk ıstedıgımız adresı alır parametre ıle 
//   },[])
useEffect(()=>{
  axios.get('https://restcountries.com/v3.1/all')
  .then(response=>setCountrie(response.data))
  .catch(error=>console.log(error));
}, []);

  return (
    <div className="App">
     <h1>fetch()-axios dersleri</h1>
     {countries.map((country=>{
      return(
        <div key={country.name}>
<h2> {country.name} </h2>
<h4>{country.capital} </h4>
<img src={country.flag} alt={country.name} style={{width:"100px"}} ></img>

        </div>
      )
     }))}
    </div>
  );
}

export default App;
