const fetchData= async()=>{
const response=await     axios
.get('https://jsonplaceholder.typicode.com/comments');
 setCountrie(response.data);
  useEffect(() => {
 fetchData()
  }, [queryText]);
}
  return (
    <div className="App">
       <input value={queryText} placeholder="ara" onChange={(e)=>{setQueryText(e.target.value)}} className="fgh"/> 
      <h1>fetch()-axios dersleri</h1>
      {countries.map(data => {
        return (
          <div key={data.name}>
            <h2> {data.name} </h2>
            <h4>{data.email} </h4>
            <p>
            {data.body}
            </p>
          </div>
        )
      })}
    </div>
  );
}

export default App;
